sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) 
{
	if ( newValue == '') 
		return;

	var attribute = g_form.getValue('u_choice_8');
	g_form.clearOptions('u_choice_8');

	if(newValue == 'legal')
	{
		g_form.addOption('u_choice_8', 'lawyerings', 'Lawyerings');
		g_form.addOption('u_choice_8', 'criminal', 'Criminal');
		g_form.addOption('u_choice_8', 'other', 'Other');
	}

	if(newValue == 'computing')
	{
		g_form.addOption('u_choice_8', 'keyboard', 'Keyboard');
		g_form.addOption('u_choice_8', 'mice', 'Mice');
		g_form.addOption('u_choice_8', 'other', 'Other');
	}

	if(newValue == 'accounting')
	{
		g_form.addOption('u_choice_8', 'tax', 'Tax');
		g_form.addOption('u_choice_8', 'money', 'Money');
		g_form.addOption('u_choice_8', 'other', 'Other');
	}

	if(newValue == 'other')
	{
		g_form.addOption('u_choice_8', 'other', 'Other');
	}

	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_choice_8', attribute);
	}

}
