sys_script_include.script:
var UtilityScript = Class.create();
UtilityScript.prototype = {
    initialize: function() {
    },
	isDatePast: function(strDate)
	{
		var currentTime = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);
		if(testDate.before(currentTime))
			{
				return true;
			}
		else
			{
				return false;
			}
	},
	isDateToday: function(strDate)
	{
		var currentTime = new GlideDateTime();
		var testDate = new GlideDateTime(strDate);
		if(testDate == currentTime)
			{
				return true;
			}
		else
			{
				return false;
			}
	},
    type: 'UtilityScript'
};
